    //         id="sleeping-pattern-1"
    //       />
    //       </Control>
    //       </Group>

    //       <Group>
    //     <Label>Prior School</Label>
    //     <Control>
    //       <input
    //         ref={register({
    //           required: "Please enter a prior school",
    //           })}
    //         type="text"
    //         placeholder="Prior School"
    //         id="prior-school"
    //       />
    //     </Control>
    //   </Group>

    //   <Group>
    //     <Label>Team</Label>
    //     <Control>
    //       <input
    //         ref={register({
    //           required: "Please enter a team",
    //           })}
    //         type="text"
    //         placeholder="Team"
    //         id="team"
    //       />
    //     </Control>
    //   </Group>

    //     <Button type="submit">Submit Student</Button>
    // </form>
  );
}

export default IntakeForm;
