import React, { useEffect } from "react";
import Strategy from "./strategy";
import { useSelector, useDispatch } from "react-redux";
import { getStrategies } from "./strategySlice.js";
import { motion } from "framer-motion";
import "bulma/css/bulma.min.css";

const Strategies = () => {
  // // pull strategies from the backend api

  const dispatch = useDispatch();
  const strategy = useSelector((state) => state.strategy.strategies);

  useEffect(() => {
    dispatch(getStrategies());
  }, [dispatch]);

  // group strategies by tier include name, description, category, tier, reference, and id
  const tier1 = strategy.filter((strategy) => strategy.tier === 1);
  const tier2 = strategy.filter((strategy) => strategy.tier === 2);
  const tier3 = strategy.filter((strategy) => strategy.tier === 3);

  return (
    <div className="strategies-container">
      <div className="columns">
        <div className="column">
          <h1 className="title">Tier 1 Strategies</h1>
          {tier1.map((strategy) => (
            <Strategy key={strategy.id} 
            name={strategy.name}
            description={strategy.description}
            category={strategy.category}
            tier={strategy.tier}
            reference={strategy.reference}
            id={strategy.id}
            />
          ))}
        </div>
        <div className="column">
          <h1 className="title">Tier 2 Strategies</h1>
          {tier2.map((strategy) => (
            <Strategy key={strategy.id}
            name={strategy.name}
            description={strategy.description}
            category={strategy.category}
            tier={strategy.tier}
            reference={strategy.reference}
            id={strategy.id}
            />
          ))}
        </div>
        <div className="column">
          <h1 className="title">Tier 3 Strategies</h1>
          {tier3.map((strategy) => (
            <Strategy key={strategy.id}

            name={strategy.name}
            description={strategy.description}
            category={strategy.category}
            tier={strategy.tier}
            reference={strategy.reference}
            id={strategy.id}
            />

          ))}
        </div>
      </div>
    </div>
  );
};


