const { Group, Label, Control } = { ...Form };

function IntakeForm() {
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm();
  const onSubmit = (data) => console.log(data);
  console.log(errors);


//   const [full_name, setName] = useState("Enter student name");
