import React, {useEffect} from "react";
import { useForm} from "react-hook-form";
import { useDispatch} from "react-redux";
import "bulma/css/bulma.min.css";
import { progressCircle } from "../utilityComponents/progressCircle";
import { Form, Button, Progress, Field } from "react-bulma-components";

import { addStrategy } from "./strategySlice";

export function StrategyForm() {

  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm();

  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(addStrategy());
  }
  , [dispatch]);


  // move strategy to "in review" status for each submitted strategy once the strategy is submitted

  const onSubmit = (data) => {
    alert(JSON.stringify(data));
  };

  const handleNewInputChange = (e) => {
    const { name, value } = e.target;
    setNewInput({ ...newInput, [name]: value });
  };

  // add in a way to sort by status
  return (
    <form onSubmit={handleSubmit(onSubmit)}>
      <input

        type="text"
        placeholder="Strategy Name"
        id="strategy-name"
        {...register("Name", {
          required: "Required",
        })}
      />

      <input

        type="text"
        placeholder="Description"
        id="strategy-description"
        {...register("Description", {
          required: "Required",
        })}
      />

      <input

        type="text"
        placeholder="Reference"
        id="strategy-reference"
        {...register("Reference", {
          required: "Required",
        })}
      />

      <input

        type="number "
        placeholder="Category"
        id="strategy-category"
        {...register("Category", {
          required: "Required",
        })}

      />

      <input
        type="number"
        placeholder="Tier"
        id="strategy-tier"
        {...register("Tier", {
          required: "Required",
        })}
      />

      <input type="submit" value="Submit" />
    </form>
  );
}
