import React, { useEffect, useState, useRef } from "react";
import { useForm } from "react-hook-form";
import { useDispatch } from "react-redux";
import "bulma/css/bulma.min.css";
import { progressCircle } from "../utilityComponents/progressCircle";
import { Form, Button } from "react-bulma-components";
import { addStrategy } from "../../actions/actions";
export function StrategyForm({}) {
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm();

  const { Label, Control, Input, Select, Textarea, Checkbox, Field } = {
    ...Form,
  };

  const dispatch = useDispatch();

  // to do: move strategy to "in review" status for each submitted strategy once the strategy is submitted

  const onSubmit = (e) => {
    e.preventDefault();
    dispatch(addStrategy(e));
  };

  const [strategy, setstrategy] = useState("");
  const [tier, setTier] = useState("");
  const [description, setDescription] = useState("");
  const [status, setStatus] = useState("");
  const [strategyId, setStrategyId] = useState("");
  const [strategyName, setStrategyName] = useState("");
  const [category, setCategory] = useState("");
  const [addRequestStatus, setAddRequestStatus] = useState('idle')

  // const handleOnChange = (e) => {
  //   setstrategy(e.target.value);
  // };

  // const updateStrategy = (event) => {
  //   setstrategy(event.target.value);
  // };

  // {...register("Name", {
  //   required:{
  //     value: true,
  //     message: "Please enter a strategy name"
  //     }
  //   })}

  // register("Tier", {
  //   required: {
  //     value: true,
  //     message: "Please select a tier",
  //   },
  // });

  // register("Description", {
  //   required: {
  //     value: true,
  //     message: "Please enter a description",
  //   },
  // });

  // register("Category", {
  //   required: {
  //     value: true,
  //     message: "Please select a category",
  //   },
  // });

  // register("Reference", {
  //   required: {
  //     value: true,
  //     message: "Please enter a reference",
  //   },
  // });

  // add in a way to sort by status
  return (
    <div className="strategy-form">
      <div className="columns">
        <div className="column is-half">
          <div className="field">
            <label className="label">Strategy Name</label>
            <div className="control">
              <input
                className="input"
                type="text"
                placeholder="Strategy Name"
                name="strategyName"
                onChange={(e) => setStrategyName(e.target.value)}
                ref={register({
                  required: {
                    value: true,
                    message: "Please enter a strategy name",
                  },
                })}
              />
            </div>
          </div>
          <div className="field">
            <label className="label">Strategy Tier</label>
            <div className="control">
              <div className="select">
                <select
                  name="strategyTier"
                  onChange={(e) => setTier(e.target.value)}
                  ref={register({
                    required: {
                      value: true,
                      message: "Please select a tier",
                    },
                  })}
                >
                  <option value="">Select a tier</option>
                  <option value="1">1</option>
                  <option value="2">2</option>
                  <option value="3">3</option>
                </select>
              </div>
            </div>
          </div>
          <div className="field">
            <label className="label">Strategy Description</label>
            <div className="control">
              <textarea
                className="textarea"
                placeholder="Strategy Description"
                name="strategyDescription"
                onChange={(e) => setDescription(e.target.value)}
                ref={register({
                  required: {
                    value: true,
                    message: "Please enter a description",
                  },
                })}
              />
            </div>
          </div>
          <div className="field">
            <label className="label">Strategy Reference</label>
            <div className="control">
              <textarea
                className="textarea"
                placeholder="Strategy Reference"
                name="strategyReference"
                onChange={(e) => setReference(e.target.value)}
                ref={register({
                  required: {
                    value: true,
                    message: "Please enter a reference",
                  },
                })}
              />
            </div>
          </div>
          <div className="field">
            <label className="label">Strategy Category</label>
            <div className="control">
              <div className="select">
                <select
                  name="strategyCategory"
                  onChange={(e) => setCategory(e.target.value)}
                  ref={register({
                    required: {
                      value: true,
                      message: "Please select a category",
                    },
                  })}
                >
                  <option value="">Select a category</option>
                  <option value="social">social</option>
                  <option value="emotional">emotional</option>
                  <option value="developmental">3</option>
                </select>
              </div>
            </div>
          </div>
      </div>
      </div>
      </div>
  );
}
