import { createSlice } from 'redux-toolkit';

const usersSlice = createSlice({
    name: 'users',
    initialState: {
        users: [],
    },
    reducers: {
        setUsers: (state, action) => {
            return { ...state, users: [...action.payload] };
        }
    }
});


export const { setUsers } = usersSlice.actions;


export default usersSlice.reducer;
