import React, {useEffect} from "react";
import Strategy from "./strategy";
import { useSelector , useDispatch } from 'react-redux';
import{ getStrategies, postStrategies } from "./strategySlice.js";
import "bulma/css/bulma.min.css";


const Strategies = () => {

  const strategies = useSelector(state => state.strategies);

  // to do show strategies as animated carousels with framer
  // to do: add counter button for did you learn something new on landing page with animation
  // to do: when strategy is turned on it is applied to the student

// pull strategies from the backend api
  useEffect(() => {
    getStrategies();
  }
  , []);

  const dispatch = useDispatch();

  const handleSubmit = (e) => {
    e.preventDefault();
    dispatch(postStrategies(newStrategy));
  }

  const handleChange = (e) => {
    const { name, value } = e.target;
    setNewStrategy({ ...newStrategy, [name]: value });
  }

  const [newStrategy, setNewStrategy] = React.useState({
    name: "",
    description: "",
    category: "",
    reference: "",
    status: "",
    tier: "",
  });

  return (
    <div className="strategies-container">
      <h1>Strategies</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Strategy Name"
          value={newStrategy.name}
          onChange={handleChange}
          id="strategy-name"
        />
        <input
          type="text"
          placeholder="Strategy Description"
          value={newStrategy.description}
          onChange={handleChange}
          id="strategy-description"
        />
        <input
          type="text"
          placeholder="Strategy Category"
          value={newStrategy.category}
          onChange={handleChange}
          id="strategy-category"
        />
        <input
          type="text"
          placeholder="Strategy Reference"
          value={newStrategy.reference}
          onChange={handleChange}
          id="strategy-reference"
        />
        <input
          type="text"
          placeholder="Strategy Status"
          value={newStrategy.status}
          onChange={handleChange}
          id="strategy-status"
        />
        <input
          type="text"
          placeholder="Strategy Tier"
          value={newStrategy.tier}
          onChange={handleChange}
          id="strategy-tier"
        />
        <button className="btn btn-primary" type="submit">
          Add Strategy
        </button>
      </form>
      <div className="strategies-container">
        {strategies.map(strategy => (
          <Strategy key={strategy.id} strategy={strategy} />
        ))}
      </div>
    </div>
  );
}


