  //     tier: "2",
  //     category: "Category 2",
  //   },

  //   {
  //     id: 3,
  //     name: "Strategy 3",
  //     description: "Description 3",
  //     status: "Not Reviewed",
  //     tier: "3",
  //     category: "Category 3",
  //   }
  // ];
  return (
    <section>
      {strategies.map((strategy) => (
        <Strategy
          name={strategy.name}
          description={strategy.description}
          tier={strategy.tier}
          category={strategy.category}
          reference={strategy.reference}
          createdAt={strategy.createdAt}
          status={strategy.status}
          id={strategy.id}
        />
      ))}
    </section>
  );
}
