import { createSlice } from "redux-toolkit";

export const strategySlice = createSlice({
  name: "strategies",
  initialState: { strategies: [], status: null },
  reducers: {
    addStrategyCategory: (state, action) => {
      return { ...state, strategyCategory: [...action.payload] };
    },
    addStrategyStatus: (state, action) => {
      return { ...state, strategyStatus: [...action.payload] };
    },
    addStrategyTier: (state, action) => {
      return { ...state, strategyTier: [...action.payload] };
    },
    addStrategyReference: (state, action) => {
      return { ...state, strategyReference: [...action.payload] };
    },
    addStrategyStudent: (state, action) => {
      return { ...state, strategyStudent: [...action.payload] };
    },
  },
  extraReducers: (builder) => {
    builder.addCase(fetchStrategyByStatus.fulfilled, (state, action) => {
      state.strategies.push(action.payload);
    });
  },
});

export const {
  addStrategyCategory,
  addStrategyReference,
  addStrategyStatus,
  addStrategyTier,
  addStrategyStudent,
} = strategySlice.actions;

export default strategySlice.reducer;
