import React, { useEffect, useState, useRef } from "react";
import { useForm } from "react-hook-form";
import { useDispatch } from "react-redux";
import "bulma/css/bulma.min.css";
import { progressCircle } from "../utilityComponents/progressCircle";
import { Form, Button } from "react-bulma-components";
import { addStrategy } from "../../actions/actions";
export function StrategyForm({}) {
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm();

  const { Label, Control, Input, Select, Textarea, Checkbox, Field } = {
    ...Form,
  };
  const dispatch = useDispatch();
  const strategies = useSelector((state) => state.strategies);

  // to do: move strategy to "in review" status for each submitted strategy once the strategy is submitted

  const [name, setname] = useState("");
  const [tier, setTier] = useState("");
  const [description, setDescription] = useState("");
  const [status, setStatus] = useState("");
  const [strategyId, setStrategyId] = useState("");
  const [reference, setReference] = useState("");
  const [strategyName, setStrategyName] = useState("");
  const [category, setCategory] = useState("");
  const [addRequestStatus, setAddRequestStatus] = useState("idle");

  const onNameChange = (e) => {
    setStrategyName(e.target.value);
  };

  const onCategoryChange = (e) => {
    setCategory(e.target.value);
  };

  const onStrategyChange = (e) => {
    setstrategy(e.target.value);
  };

  const onTierChange = (e) => {
    setTier(e.target.value);
  };

  const onDescriptionChange = (e) => {
    setDescription(e.target.value);
  };

  const onReferenceChange = (e) => {
    setReference(e.target.value);
  };

  const onSubmit = (e) => {
    e.preventDefault();
    dispatch(addStrategy(e));
  };


  const canSave = [name, category, strategy, tier, description].every(Boolean) && addRequestStatus === "idle";

  const OnSaveStrategyClick = async () => {
    if (canSave) {
      try {
        setAddRequestStatus("pending");
        await dispatch(addStrategy({ name, category, strategy, tier, description, strategy: strategyId })).unwrap()
        setName("");
        setCategory("");
        setTier("");
        setDescription("");
        setStrategyId("");
      } catch (error) {
        setAddRequestStatus("failed");
      } finally {
        setAddRequestStatus("idle");
      }
    }

  }

  // add in a way to sort by status
  return (
    <form>
      <Form.Field>
        <Label size="medium"> Strategy Name</Label>
        <Control>
          <Input
            type="text"
            placeholder="Strategy Name"
            id="strategy-name"
            value={name}
            onChange={onNameChange}
          />
        </Control>
      </Form.Field>
      <Form.Field>
        <Label size="medium"> Strategy Description</Label>
        <Control>
          <Input
            type="text"
            placeholder="Description"
            value={description}
            onChange={onDescriptionChange}
            id="strategy-description"
          />
        </Control>
      </Form.Field>
      <Form.Field>
        <Label size="medium"> Strategy Reference</Label>
        <Control>
          <Input
            type="text"
            placeholder="Reference"
            value={reference}
            onChange={onReferenceChange}
            id="strategy-reference"
          />
        </Control>
      </Form.Field>
      <Form.Field>
        <Label size="medium"> Strategy Category</Label>
        <Control>
          <Select
            type="number "
            placeholder="Category"
            value={category}
            onChange={onCategoryChange}
            id="strategy-category"
          >
            <option value="1">Academic</option>
            <option value="2">Social</option>
            <option value="3">Emotional</option>
            <option value="4">Developmental</option>
          </Select>
        </Control>
      </Form.Field>
      <Form.Field>
        <Label size="medium"> Strategy Tier </Label>
        <Control>
          <Select
            type="number"
            placeholder="Tier"
            value={tier}
            onChange={onTierChange}
            id="strategy-tier"
          >
            <option>Tier 1</option>
            <option>Tier 2</option>
            <option>Tier 3</option>
          </Select>
        </Control>

        {/* <Field>
          <Control
            type="number"
            placeholder="Tier"
            id="strategy-tier"
            {...register("Tier", {
              required: "Required",
            })}
          >
            <Checkbox>Tier 1 </Checkbox>
            <br />
            <Checkbox>Tier 2 </Checkbox>
            <br />
            <Checkbox>Tier 3 </Checkbox>
          </Control>
        </Field> */}
      </Form.Field>
      <br />
      <Form.Field>
        <Button color="primary" type="submit" onClick={OnSaveStrategyClick} disabled={!canSave}>
          Submit
        </Button>
      </Form.Field>
    </form>
  );
}



