import React, { useEffect } from "react";
import Strategy from "./strategy";
import { useSelector, useDispatch } from "react-redux";
import { getStrategies } from "./strategySlice.js";
import { useScroll } from "react-use-gesture";
import {motion, useAnimation} from 'framer-motion';
import { useSpring, animated } from "react-spring";


import "bulma/css/bulma.min.css";
const clamp = (value, min, max) => Math.max(Math.min(value, max), min);

const Strategies = () => {
  // // pull strategies from the backend api

  const dispatch = useDispatch();
  const strategy = useSelector((state) => state.strategy.strategies);

  useEffect(() => {
    dispatch(getStrategies());
  }, [dispatch]);


  const controls = useAnimation();
  const bind = useScroll(event => {
   control.start({ 
     transform: `perspective(100px) rotateY(${
       event.scrolling ? clamp(event.delta[0]) : 0
      }deg)`
     });
   });


  return (
    <div className="scroll">
    <motion.div
      className="columns carousel"
    >
      <motion.div className="inner-carousel" >
        <div className="strategies-container">
          <div className="columns " {...bind()}>
            {strategy.map((strategy) => {
              <motion.div
              className="card"

              animate={controls}
              />
              return (
              <motion.div className="item">
                <Strategy
                  key={strategy.id}
                  name={strategy.name}
                  description={strategy.description}
                  reference={strategy.reference}
                  category={strategy.category}
                  tier={strategy.tier}
                />
              </motion.div>
            )
})}
          </div>
        </div>
      </motion.div>
    </motion.div>
    </div>
  );
};

export default Strategies;
