import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { motion } from "framer-motion";
import "bulma/css/bulma.min.css";
import "../../App.css";

import {
  Card,
  Media,
  Heading,
  Content,
  Image,
  Icon,
} from "react-bulma-components";
import {
  IoChatbubblesOutline,
  IoThumbsDownOutline,
  IoHeartOutline,
  IoStarOutline,
  IoEllipsisHorizontalSharp,
  IoThumbsUpOutline,
} from "react-icons/io5";

function Strategy(props) {
  // const strategy = useSelector((state) => state.strategy);

  // const dispatch = useDispatch();

  const [isOn, setIsOn] = useState(false);
  const toggleSwitch = () => setIsOn(!isOn);
  const spring = {
    type: "spring",
    stiffness: 700,
    damping: 30,
  };

  // const [count, setCount] = useState(0);
  // const [favorite, setfavorite] = useState(false);
  // const [loved, setloved] = useState(false);
  // const [disliked, setdisliked] = useState(false);
  // const [status, setstatus] = useState("Not Reviewed");
  // const [modal, setmodal] = useState(false);
  console.log(props, " my props");

  return (
    //map through the strategies and display them

    // seperate each strategy by tier in columns
    <motion.div
      className="columns carousel"
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 0.5 }}
    >
      <motion.div className="inner-carousel" >
        <div className="strategies-container">
          <div className="columns">
            <div className="item">
              <Card>
                <Card.Content>
                  <Media>
                    <Media.Item renderAs="figure" position="left">
                      <Image
                        size={64}
                        alt="64x64"
                        src="https://bulma.io/images/placeholders/128x128.png"
                      />
                    </Media.Item>
                    <Media.Item>
                      <Heading size={4}>
                        {props.name}
                        <IoEllipsisHorizontalSharp
                          className="icon"
                          onClick={toggleSwitch}
                        />
                      </Heading>
                      <Heading subtitle size={6}>
                        {props.category}
                      </Heading>
                      <Heading subtitle size={6}>
                        {props.tier}
                      </Heading>
                      <Heading subtitle size={6}>
                        {props.reference}
                      </Heading>
                      <Heading subtitle size={6}>
                        {props.description}
                      </Heading>
                    </Media.Item>
                  </Media>
                </Card.Content>
                <Card.Footer>
                  <Card.Footer.Item>
                    <Icon className="icon" color="primary">
                      <IoChatbubblesOutline />
                    </Icon>
                    <span>{props.comments}</span>
                  </Card.Footer.Item>
                  <Card.Footer.Item>
                    <Icon className="icon" color="danger">
                      <IoThumbsDownOutline />
                    </Icon>
                    <span>{props.dislikes}</span>
                  </Card.Footer.Item>
                  <Card.Footer.Item>
                    <Icon className="icon" color="success">
                      <IoThumbsUpOutline />
                    </Icon>
                    <span>{props.likes}</span>
                  </Card.Footer.Item>
                  <Card.Footer.Item>
                    <Icon className="icon" color="info">
                      <IoHeartOutline />
                    </Icon>
                    <span>{props.favorites}</span>
                  </Card.Footer.Item>
                  <Card.Footer.Item>
                    <Icon className="icon" color="info">
                      <IoStarOutline />
                    </Icon>
                    <span>{props.stars}</span>
                  </Card.Footer.Item>
                </Card.Footer>
              </Card>
            </div>
          </div>
        </div>
      </motion.div>
    </motion.div>
  );
}

