import React, { useEffect, useState, useRef } from "react";
import { useForm } from "react-hook-form";
import { useDispatch } from "react-redux";
import "bulma/css/bulma.min.css";
import { progressCircle } from "../utilityComponents/progressCircle";
import { Form, Button } from "react-bulma-components";
import { addStrategy } from "../../actions/actions";
export function StrategyForm({}) {
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm();

  const { Label, Control, Input, Select, Textarea, Checkbox, Field } = {
    ...Form,
  };

  const dispatch = useDispatch();

  // to do: move strategy to "in review" status for each submitted strategy once the strategy is submitted

  const onSubmit = (e) => {
    e.preventDefault();
    dispatch(addStrategy(e));
  };

  const [strategy, setstrategy] = useState("");

  const handleOnChange = (e) => {
    setstrategy(e.target.value);
  };

  const updateStrategy = (event) => {
    setstrategy(event.target.value);
  };

  // {...register("Name", {
  //   required:{
  //     value: true,
  //     message: "Please enter a strategy name"
  //     }
  //   })}

  // register("Tier", {
  //   required: {
  //     value: true,
  //     message: "Please select a tier",
  //   },
  // });

  // register("Description", {
  //   required: {
  //     value: true,
  //     message: "Please enter a description",
  //   },
  // });

  // register("Category", {
  //   required: {
  //     value: true,
  //     message: "Please select a category",
  //   },
  // });

  // register("Reference", {
  //   required: {
  //     value: true,
  //     message: "Please enter a reference",
  //   },
  // });

  // add in a way to sort by status
  return (
    <form className="simpleform" value={strategy}>
      <Form.Field>
        <Label size="medium"> Strategy Name</Label>
        <Control>
          <Input
            type="text"
            placeholder="Strategy Name"
            onChange={handleOnChange}
            id="strategy-name"
          />
        </Control>
      </Form.Field>
      <Form.Field>
        <Label size="medium"> Strategy Description</Label>
        <Control>
          <Input
            type="text"
            placeholder="Description"
            onChange={handleOnChange}
            id="strategy-description"
          />
        </Control>
      </Form.Field>
      <Form.Field>
        <Label size="medium"> Strategy Reference</Label>
        <Control>
          <Input
            type="text"
            placeholder="Reference"
            onChange={handleOnChange}
            id="strategy-reference"
          />
        </Control>
      </Form.Field>
      <Form.Field>
        <Label size="medium"> Strategy Category</Label>
        <Control>
          <Select
            type="number "
            placeholder="Category"
            onChange={handleOnChange}
            id="strategy-category"
          >
            <option value="1">Academic</option>
            <option value="2">Social</option>
            <option value="3">Emotional</option>
            <option value="4">Developmental</option>
          </Select>
        </Control>
      </Form.Field>
      <Form.Field>
        <Label size="medium"> Strategy Tier </Label>
        <Control>
          <Select
            type="number"
            placeholder="Tier"
            onChange={handleOnChange}
            id="strategy-tier"
          >
            <option>Tier 1</option>
            <option>Tier 2</option>
            <option>Tier 3</option>
          </Select>
        </Control>

        {/* <Field>
          <Control
            type="number"
            placeholder="Tier"
            id="strategy-tier"
            {...register("Tier", {
              required: "Required",
            })}
          >
            <Checkbox>Tier 1 </Checkbox>
            <br />
            <Checkbox>Tier 2 </Checkbox>
            <br />
            <Checkbox>Tier 3 </Checkbox>
          </Control>
        </Field> */}
      </Form.Field>
      <br />
      <Form.Field>
        <Button color="primary" type="submit" onClick={onSubmit}>
          Submit
        </Button>
      </Form.Field>
    </form>
  );
}
