import React, {useEffect, useState} from "react";
import Strategy from "./strategy";
import { useSelector , useDispatch } from 'react-redux';
import{ getStrategies, postStrategies } from "./strategySlice.js";
import "bulma/css/bulma.min.css";


export default function Strategies(props) {

  const [isLoading, setIsLoading] = useState(false);
const [loadedMeetups, setLoadedMeetups] = useState([]);

  const strategies = useSelector(state => state.strategies);

  // to do show strategies as animated carousels with framer
  // to do: add counter button for did you learn something new on landing page with animation
  // to do: when strategy is turned on it is applied to the student

// pull strategies from the backend api
const dispatch = useDispatch();
  useEffect(() => {
    dispatch(getStrategies()
    .then(strategies => {
      setLoadedMeetups(strategies);
      setIsLoading(false);
    })

    );

  }, [dispatch]);


  console.log(strategies);
  return (
    <section>
      {props.strategies.map((strategy) => (
        <Strategy
          name={strategy.name}
          description={strategy.description}
          tier={strategy.tier}
          category={strategy.category}
          reference={strategy.reference}
          createdAt={strategy.createdAt}
          id={strategy.id}
        />
      ))}
    </section>
  );
}
