import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { motion } from "framer-motion";
import "bulma/css/bulma.min.css";
import "../../App.css";

import {
  Card,
  Media,
  Heading,
  Content,
  Image,
  Icon,
} from "react-bulma-components";
import {
  IoChatbubblesOutline,
  IoThumbsDownOutline,
  IoHeartOutline,
  IoStarOutline,
  IoEllipsisHorizontalSharp,
} from "react-icons/io5";


function Strategy(props) {
  // const strategy = useSelector((state) => state.strategy);

  const dispatch = useDispatch();

  const [isOn, setIsOn] = useState(false);
  const toggleSwitch = () => setIsOn(!isOn);
  const spring = {
    type: "spring",
    stiffness: 700,
    damping: 30,
  };

  // const [count, setCount] = useState(0);
  // const [favorite, setfavorite] = useState(false);
  // const [loved, setloved] = useState(false);
  // const [disliked, setdisliked] = useState(false);
  // const [status, setstatus] = useState("Not Reviewed");
  // const [modal, setmodal] = useState(false);

  return (
    //map through the strategies and display them
    <Card className="strategy-card">
      <Card.Content>
        <Media>
          <Media.Item>
            <Image
              size={64}
              alt="64x64"
              src="https://bulma.io/images/placeholders/128x128.png"
            />
          </Media.Item>
          <Media.Item>
            <Heading size={4}>{props.strategy.name}</Heading>
            <Heading subtitle size={6}>
              {props.strategy.description}
            </Heading>

            <Heading subtitle size={6}>
              {props.strategy.category}
            </Heading>
          </Media.Item>
        </Media>
      </Card.Content>
      <Card.Content>
        <Content>
          <p>
            <strong>{props.strategy.description}</strong>
          </p>
        </Content>
      </Card.Content>
      <Card.Footer>
        <Card.Footer.Item>
          <Icon className="icon" size={1}>
            <IoChatbubblesOutline />\
          </Icon>
          <span>{props.strategy.comments}</span>
        </Card.Footer.Item>
        <Card.Footer.Item>
          <Icon className="icon" size={1}>
            <IoThumbsDownOutline />
          </Icon>
          <span>{props.strategy.dislikes}</span>
        </Card.Footer.Item>
        <Card.Footer.Item>
          <Icon className="icon" size={1}>
            <IoHeartOutline />
          </Icon>
          <span>{props.strategy.reference}</span>
        </Card.Footer.Item>
    </Card.Footer>
    </Card>


  );
}

export default Strategy;
