import React, {useEffect} from "react";
import { useForm} from "react-hook-form";
import { useDispatch} from "react-redux";
import "bulma/css/bulma.min.css";
import { progressCircle } from "../utilityComponents/progressCircle";
import { Form, Button, Progress, Field , Label} from "react-bulma-components";

import { addStrategy } from "./strategySlice";

export function StrategyForm() {

  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm();

  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(addStrategy());
  }
  , [dispatch]);


  // move strategy to "in review" status for each submitted strategy once the strategy is submitted

  const onSubmit = (data) => {
    alert(JSON.stringify(data));
  };

  const handleNewInputChange = (e) => {
    const { name, value } = e.target;
    setNewInput({ ...newInput, [name]: value });
  };

  // add in a way to sort by status
  return (
    <form onSubmit={handleSubmit(onSubmit)}>
      <Form.Field>
        <Label size="medium"> Strategy Name</Label>
        <Control>

      <input

        type="text"
        placeholder="Strategy Name"
        id="strategy-name"
        {...register("Name", {
          required: "Required",
        })}
      />
        </Control>
      </Form.Field>
      <Form.Field>
        <Label size="medium"> Strategy Description</Label>
        <Control>

      <input

        type="text"
        placeholder="Description"
        id="strategy-description"
        {...register("Description", {
          required: "Required",
        })}
      />
        </Control>
      </Form.Field>
      <Form.Field>
        <Label size="medium"> Strategy Reference</Label>
        <Control>

      <input

        type="text"
        placeholder="Reference"
        id="strategy-reference"
        {...register("Reference", {
          required: "Required",
        })}
      />

        </Control>
      </Form.Field>
      <Form.Field>
        <Label size="medium"> Strategy Category</Label>
        <Control>

      <input

        type="number "
        placeholder="Category"
        id="strategy-category"
        {...register("Category", {
          required: "Required",
        })}

      />
        </Control>
      </Form.Field>
      <Form.Field>
        <Label size="medium"> Strategy Tier </Label>
        <Control>

      <input
        type="number"
        placeholder="Tier"
        id="strategy-tier"
        {...register("Tier", {
          required: "Required",
        })}
      />
        </Control>
      </Form.Field>

      <input type="submit" value="Submit" />
    </form>
  );
}
