import React, { useEffect } from "react";
import Strategy from "./strategy";
import { useSelector, useDispatch } from "react-redux";
import { getStrategies } from "./strategySlice.js";
import "bulma/css/bulma.min.css";

const Strategies = () => {


  // pull strategies from the backend api

  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(getStrategies())
  }, [dispatch])

  const strategies = useSelector(getStrategies);

  console.log(strategies)
//pass strategies into the strategy component
  return (
    <div className="strategies">
      <div className="container">
        <div className="columns is-mobile">
          {strategies.map((strategy) => (
            <Strategy key={strategy.id} strategy={strategy} />
          ))}
        </div>
      </div>
    </div>
  );
}


export default Strategies;
